{
    "name": "TurnoDNI12",
    "description": "Este wizard permite gestionar turnos para el DNI",
    "initialStep": "first",
    "steps": [
        {
            "name": "first",
            "title": "Paso 1. Datos del paciente",
            "fields": [
                {
                    "order": 1,
                    "name": "dni",
                    "label": {
                        "value": "DNI",
                        "position": "LEFT"
                    },
                    "placeholder": "Ingrese el DNI",
                    "type": "NUMBER",
                    "helpText": "El DNI es un número que puede encontrar en su documento de identidad",
                    "rightButton": {
                        "name": "reload",
                        "label": "Recargar",
                        "icon": "X",
                        "events": {
                            "ON_CLICK": [
                                {
                                    "order": 1,
                                    "conditions": {
                                        "isEmpty": {
                                            "path": "dni"
                                        }
                                    },
                                    "then": [
                                        {
                                            "order": 1,
                                            "type": "ADD_ERROR",
                                            "fieldName": "dni",
                                            "errorMessage": "El DNI es requerido"
                                        }
                                    ]
                                },
                                {
                                    "order": 2,
                                    "conditions": {
                                        "not": {
                                            "matchesRegex": {
                                                "path": "dni",
                                                "value": "^[0-9]{7,8}$"
                                            }
                                        }
                                    },
                                    "then": [
                                        {
                                            "order": 1,
                                            "type": "ADD_ERROR",
                                            "fieldName": "dni",
                                            "errorMessage": "El dni no es válido"
                                        }
                                    ]
                                },
                                {
                                    "order": 3,
                                    "conditions": {
                                        "and": [
                                            {
                                                "not": {
                                                    "isEmpty": {
                                                        "path": "dni"
                                                    }
                                                }
                                            },
                                            {
                                                "matchesRegex": {
                                                    "path": "dni",
                                                    "value": "^[0-9]{7,8}$"
                                                }
                                            }
                                        ]
                                    },
                                    "then": [
                                        {
                                            "order": 1,
                                            "type": "SET_VALUE",
                                            "targetName": "name",
                                            "value": "Octavio Alcalde"
                                        },
                                        {
                                            "order": 2,
                                            "type": "CLEAR_ERRORS",
                                            "fieldName": "name"
                                        },
                                        {
                                            "order": 3,
                                            "type": "UPDATE_COMPONENT",
                                            "targetName": "name",
                                            "targetProp": "disabled",
                                            "value": false
                                        }
                                    ]
                                }
                            ]
                        }
                    },
                    "events": {
                        "ON_CHANGE": [
                            {
                                "order": 1,
                                "conditions": true,
                                "then": [
                                    {
                                        "order": 1,
                                        "type": "CLEAR_ERRORS",
                                        "fieldName": "dni"
                                    }
                                ]
                            }
                        ]
                    }
                },
                {
                    "order": 2,
                    "name": "name",
                    "label": {
                        "value": "Nombre del paciente",
                        "position": "TOP"
                    },
                    "type": "TEXT"
                },
                {
                    "order": 3,
                    "name": "genre",
                    "label": {
                        "value": "Sexo",
                        "position": "TOP"
                    },
                    "placeholder": "Ingrese el sexo",
                    "type": "SELECT",
                    "options": [],
                    "events": {
                        "ON_MOUNTED": [
                            {
                                "order": 1,
                                "conditions": true,
                                "then": [
                                    {
                                        "order": 1,
                                        "type": "UPDATE_COMPONENT",
                                        "targetName": "genre",
                                        "targetProp": "options",
                                        "value": [
                                            {
                                                "value": "female",
                                                "label": "Femenino"
                                            },
                                            {
                                                "value": "male",
                                                "label": "Masculino"
                                            }
                                        ]
                                    }
                                ]
                            }
                        ]
                    }
                },
                {
                    "order": 4,
                    "name": "character",
                    "label": {
                        "value": "Personaje",
                        "position": "TOP"
                    },
                    "placeholder": "Ingrese el personaje",
                    "type": "SELECT",
                    "options": [],
                    "events": {
                        "ON_MOUNTED": [
                            {
                                "order": 1,
                                "conditions": true,
                                "then": [
                                    {
                                        "order": 1,
                                        "type": "REMOTE_UPDATE_COMPONENT",
                                        "targetName": "character",
                                        "targetProp": "options",
                                        "name": "getCharacters"
                                    }
                                ]
                            }
                        ]
                    }
                }
            ],
            "events": {
                "ON_MOUNTED": [
                    {
                        "order": 1,
                        "conditions": true,
                        "then": [
                            {
                                "order": 1,
                                "type": "INIT_STEP_VALUES"
                            }
                        ]
                    },
                    {
                        "order": 2,
                        "conditions": {
                            "isEmpty": {
                                "path": "dni"
                            }
                        },
                        "then": [
                            {
                                "order": 1,
                                "type": "UPDATE_COMPONENT",
                                "targetName": "name",
                                "targetProp": "disabled",
                                "value": true
                            }
                        ]
                    }
                ]
            },
            "rightButton": {
                "name": "next",
                "label": "Siguiente",
                "events": {
                    "ON_CLICK": [
                        {
                            "order": 1,
                            "conditions": true,
                            "then": [
                                {
                                    "order": 1,
                                    "type": "UPDATE_COMPONENT",
                                    "targetName": "next",
                                    "targetProp": "disabled",
                                    "value": true
                                },
                                {
                                    "order": 2,
                                    "type": "UPDATE_COMPONENT",
                                    "targetName": "next",
                                    "targetProp": "loading",
                                    "value": true
                                }
                            ]
                        },
                        {
                            "order": 2,
                            "conditions": {
                                "isEmpty": {
                                    "path": "dni"
                                }
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "ADD_ERROR",
                                    "fieldName": "dni",
                                    "errorMessage": "El DNI es requerido"
                                }
                            ]
                        },
                        {
                            "order": 3,
                            "conditions": {
                                "not": {
                                    "matchesRegex": {
                                        "path": "dni",
                                        "value": "^[0-9]{7,8}$"
                                    }
                                }
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "ADD_ERROR",
                                    "fieldName": "dni",
                                    "errorMessage": "El dni no es válido"
                                }
                            ]
                        },
                        {
                            "order": 4,
                            "conditions": {
                                "isEmpty": {
                                    "path": "name"
                                }
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "ADD_ERROR",
                                    "fieldName": "name",
                                    "errorMessage": "El nombre es requerido"
                                }
                            ]
                        },
                        {
                            "order": 5,
                            "conditions": {
                                "and": [
                                    {
                                        "not": {
                                            "isEmpty": {
                                                "path": "dni"
                                            }
                                        }
                                    },
                                    {
                                        "matchesRegex": {
                                            "path": "dni",
                                            "value": "^[0-9]{7,8}$"
                                        }
                                    },
                                    {
                                        "not": {
                                            "isEmpty": {
                                                "path": "name"
                                            }
                                        }
                                    },
                                    {
                                        "remote": {
                                            "name": "mortyAnda",
                                            "context": [
                                                {
                                                    "key": "dni",
                                                    "type": "CURRENT_STEP_VALUES",
                                                    "fieldName": "dni"
                                                }
                                            ]
                                        }
                                    }
                                ]
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "SAVE_STEP_VALUES"
                                },
                                {
                                    "order": 3,
                                    "type": "GO_TO_STEP",
                                    "stepName": "secondA"
                                }
                            ]
                        },
                        {
                            "order": 6,
                            "conditions": {
                                "and": [
                                    {
                                        "not": {
                                            "isEmpty": {
                                                "path": "dni"
                                            }
                                        }
                                    },
                                    {
                                        "matchesRegex": {
                                            "path": "dni",
                                            "value": "^[0-9]{7,8}$"
                                        }
                                    },
                                    {
                                        "not": {
                                            "remote": {
                                                "name": "mortyAnda",
                                                "context": [
                                                    {
                                                        "key": "dni",
                                                        "type": "CURRENT_STEP_VALUES",
                                                        "fieldName": "dni"
                                                    }
                                                ]
                                            }
                                        }
                                    }
                                ]
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "GO_TO_STEP",
                                    "stepName": "secondB"
                                }
                            ]
                        },
                        {
                            "order": 7,
                            "conditions": true,
                            "then": [
                                {
                                    "order": 1,
                                    "type": "UPDATE_COMPONENT",
                                    "targetName": "next",
                                    "targetProp": "disabled",
                                    "value": false
                                },
                                {
                                    "order": 2,
                                    "type": "UPDATE_COMPONENT",
                                    "targetName": "next",
                                    "targetProp": "loading",
                                    "value": false
                                }
                            ]
                        }
                    ]
                }
            }
        },
        {
            "name": "secondA",
            "title": "Paso 2. Fecha del turno",
            "fields": [
                {
                    "order": 1,
                    "name": "turnDate",
                    "label": {
                        "value": "Fecha de turno",
                        "position": "TOP"
                    },
                    "type": "DATE"
                }
            ],
            "events": {},
            "leftButton": {
                "name": "previous",
                "label": "Anterior",
                "events": {
                    "ON_CLICK": [
                        {
                            "order": 1,
                            "conditions": true,
                            "then": [
                                {
                                    "order": 1,
                                    "type": "GO_TO_STEP",
                                    "stepName": "first"
                                }
                            ]
                        }
                    ]
                }
            },
            "rightButton": {
                "name": "terminate",
                "label": "Terminar",
                "events": {
                    "ON_CLICK": [
                        {
                            "order": 1,
                            "conditions": true,
                            "then": [
                                {
                                    "order": 1,
                                    "type": "SAVE_STEP_VALUES"
                                },
                                {
                                    "order": 3,
                                    "type": "FINISH_WIZARD"
                                }
                            ]
                        }
                    ]
                }
            }
        },
        {
            "name": "secondB",
            "title": "Paso 2. Más sobre usted",
            "fields": [
                {
                    "order": 1,
                    "name": "name",
                    "label": {
                        "value": "Nombre del paciente",
                        "position": "TOP"
                    },
                    "type": "TEXT"
                }
            ],
            "events": {},
            "leftButton": {
                "name": "previous",
                "label": "Anterior",
                "events": {
                    "ON_CLICK": [
                        {
                            "order": 1,
                            "conditions": true,
                            "then": [
                                {
                                    "order": 1,
                                    "type": "GO_TO_STEP",
                                    "stepName": "first"
                                }
                            ]
                        }
                    ]
                }
            },
            "rightButton": {
                "name": "next",
                "label": "Siguiente",
                "events": {
                    "ON_CLICK": [
                        {
                            "order": 1,
                            "conditions": {
                                "not": {
                                    "isEmpty": {
                                        "path": "name"
                                    }
                                }
                            },
                            "then": [
                                {
                                    "order": 1,
                                    "type": "GO_TO_STEP",
                                    "stepName": "secondA"
                                }
                            ]
                        }
                    ]
                }
            }
        }
    ]
}

{
    "name": "TurnoPasaporteConPago",
    "description": "Este wizard permite gestionar turnos para el pasaporte",
    "isActive": true,
    "initialStep": "init",
    "steps": [
        {
            "name": "A",
            "description": "Se obtiene la oficina y preferencia",
            "components": [
                {
                    "order": 5,
                    "name": "cabecera-paso-1",
                    "componentType": "TEXT",
                    "component": {
                        "value": "{{content}}",
                        "contexts": [
                            {
                                "key": "content",
                                "type": "LOCAL",
                                "expression": "data.dscGeneral",
                                "contextName": "step1"
                            }
                        ]
                    }
                },
                {
                    "order": 10,
                    "name": "labelOficina",
                    "componentType": "LABEL",
                    "component": {
                        "value": "Oficina"
                    }
                },
                {
                    "order": 15,
                    "name": "oficina",
                    "componentType": "FIELD",
                    "component": {
                        "type": "SELECT",
                        "placeholder": "Ingrese la oficina",
                        "save": false,
                        "options": []
                    },
                    "events": {
                        "ON_CHANGE": [
                            {
                                "then": [
                                    {
                                        "type": "CLEAR_ERRORS",
                                        "order": 1,
                                        "fieldName": "oficina"
                                    }
                                ],
                                "order": 1,
                                "conditions": true
                            }
                        ]
                    }
                },
                {
                    "order": 20,
                    "name": "labelPreferencia",
                    "componentType": "LABEL",
                    "component": {
                        "value": "Preferencia del turno"
                    }
                },
                {
                    "order": 25,
                    "name": "preferencia",
                    "componentType": "FIELD",
                    "component": {
                        "type": "SELECT",
                        "save": false,
                        "options": []
                    },
                    "events": {
                        "ON_CHANGE": [
                            {
                                "then": [
                                    {
                                        "type": "CLEAR_ERRORS",
                                        "order": 1,
                                        "fieldName": "preferencia"
                                    }
                                ],
                                "order": 1,
                                "conditions": true
                            }
                        ]
                    }
                },
                {
                    "order": 30,
                    "name": "next",
                    "componentType": "BUTTON",
                    "component": {
                        "label": "Siguiente"
                    },
                    "events": {
                        "ON_CLICK": [
                            {
                                "then": [
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 1,
                                        "context": {
                                            "key": "disabled",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "next",
                                        "targetProp": "disabled"
                                    },
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 2,
                                        "context": {
                                            "key": "loading",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "next",
                                        "targetProp": "loading"
                                    }
                                ],
                                "order": 0,
                                "conditions": true
                            },
                            {
                                "then": [
                                    {
                                        "type": "ADD_ERROR",
                                        "order": 1,
                                        "fieldName": "oficina",
                                        "errorMessage": "La oficina es requerida"
                                    }
                                ],
                                "order": 1,
                                "conditions": {
                                    "isEmpty": {
                                        "context": {
                                            "key": "oficina",
                                            "type": "CURRENT_STEP_VALUES",
                                            "fieldName": "oficina"
                                        }
                                    }
                                }
                            },
                            {
                                "then": [
                                    {
                                        "type": "ADD_ERROR",
                                        "order": 1,
                                        "fieldName": "preferencia",
                                        "errorMessage": "La preferencia es requerida"
                                    }
                                ],
                                "order": 2,
                                "conditions": {
                                    "isEmpty": {
                                        "context": {
                                            "key": "preferencia",
                                            "type": "CURRENT_STEP_VALUES",
                                            "fieldName": "preferencia"
                                        }
                                    }
                                }
                            },
                            {
                                "then": [
                                    {
                                        "type": "SET_LOCAL_CONTEXT",
                                        "order": 2,
                                        "context": {
                                            "key": "step2",
                                            "type": "REMOTE_ACTION",
                                            "contexts": [
                                                {
                                                    "key": "tramite",
                                                    "type": "VALUE",
                                                    "value": "Pasaporte"
                                                },
                                                {
                                                    "key": "paso",
                                                    "type": "VALUE",
                                                    "value": 2
                                                },
                                                {
                                                    "key": "cuil",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "cuil"
                                                },
                                                {
                                                    "key": "nombres",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "nombres"
                                                },
                                                {
                                                    "key": "apellidos",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "apellidos"
                                                },
                                                {
                                                    "key": "sexo",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "sexo"
                                                },
                                                {
                                                    "key": "fechaNacimiento",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "fechaNacimiento"
                                                },
                                                {
                                                    "key": "departamento",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "departamento"
                                                },
                                                {
                                                    "key": "correo",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "correo"
                                                },
                                                {
                                                    "key": "telefono",
                                                    "type": "EXTERNAL_METADATA",
                                                    "expression": "telefono"
                                                },
                                                {
                                                    "key": "oficinas",
                                                    "type": "CURRENT_STEP_VALUES",
                                                    "fieldName": "oficina",
                                                    "expression": "value"
                                                },
                                                {
                                                    "key": "preferencias",
                                                    "type": "CURRENT_STEP_VALUES",
                                                    "fieldName": "preferencia",
                                                    "expression": "value"
                                                }
                                            ],
                                            "actionName": "turnos-test"
                                        },
                                        "contextName": "step2"
                                    },
                                    {
                                        "type": "GO_TO_STEP",
                                        "order": 3,
                                        "stepName": "pagar"
                                    }
                                ],
                                "order": 3,
                                "conditions": {
                                    "and": [
                                        {
                                            "not": {
                                                "isEmpty": {
                                                    "context": {
                                                        "key": "oficina",
                                                        "type": "CURRENT_STEP_VALUES",
                                                        "fieldName": "oficina"
                                                    }
                                                }
                                            }
                                        },
                                        {
                                            "not": {
                                                "isEmpty": {
                                                    "context": {
                                                        "key": "preferencia",
                                                        "type": "CURRENT_STEP_VALUES",
                                                        "fieldName": "preferencia"
                                                    }
                                                }
                                            }
                                        }
                                    ]
                                }
                            },
                            {
                                "then": [
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 1,
                                        "context": {
                                            "key": "disabled",
                                            "type": "VALUE",
                                            "value": false
                                        },
                                        "targetName": "next",
                                        "targetProp": "disabled"
                                    },
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 2,
                                        "context": {
                                            "key": "loading",
                                            "type": "VALUE",
                                            "value": false
                                        },
                                        "targetName": "next",
                                        "targetProp": "loading"
                                    }
                                ],
                                "order": 99,
                                "conditions": true
                            }
                        ]
                    }
                }
            ]
        },
        {
            "name": "pagar",
            "description": "Se paga por el turno",
            "components": [
                {
                    "order": 5,
                    "name": "cabecera-paso-pagar",
                    "componentType": "TEXT",
                    "component": {
                        "value": "<b>Realice el pago</b>"
                    }
                },
                {
                    "order": 10,
                    "name": "previous",
                    "componentType": "BUTTON",
                    "component": {
                        "label": "Anterior"
                    },
                    "events": {
                        "ON_CLICK": [
                            {
                                "then": [
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 1,
                                        "context": {
                                            "key": "disabled",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "previous",
                                        "targetProp": "disabled"
                                    },
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 2,
                                        "context": {
                                            "key": "loading",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "previous",
                                        "targetProp": "loading"
                                    }
                                ],
                                "order": 0,
                                "conditions": true
                            },
                            {
                                "then": [
                                    {
                                        "type": "GO_TO_STEP",
                                        "order": 1,
                                        "stepName": "A"
                                    }
                                ],
                                "order": 1,
                                "conditions": true
                            },
                            {
                                "then": [
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 1,
                                        "context": {
                                            "key": "disabled",
                                            "type": "VALUE",
                                            "value": false
                                        },
                                        "targetName": "previous",
                                        "targetProp": "disabled"
                                    },
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 2,
                                        "context": {
                                            "key": "loading",
                                            "type": "VALUE",
                                            "value": false
                                        },
                                        "targetName": "previous",
                                        "targetProp": "loading"
                                    }
                                ],
                                "order": 99,
                                "conditions": true
                            }
                        ]
                    }
                },
                {
                    "order": 15,
                    "name": "next",
                    "componentType": "BUTTON",
                    "component": {
                        "label": "Pagar"
                    },
                    "events": {
                        "ON_CLICK": [
                            {
                                "then": [
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 1,
                                        "context": {
                                            "key": "disabled",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "next",
                                        "targetProp": "disabled"
                                    },
                                    {
                                        "type": "UPDATE_COMPONENT",
                                        "order": 2,
                                        "context": {
                                            "key": "loading",
                                            "type": "VALUE",
                                            "value": true
                                        },
                                        "targetName": "next",
                                        "targetProp": "loading"
                                    }
                                ],
                                "order": 0,
                                "conditions": true
                            },
                            {
                                "then": [
                                    {
                                        "type": "SET_LOCAL_CONTEXT",
                                        "order": 2,
                                        "context": {
                                            "key": "tokenPago",
                                            "type": "REMOTE_ACTION",
                                            "actionName": "ObtenerTokenPagos"
                                        },
                                        "contextName": "tokenPago"
                                    },
                                    {
                                        "type": "SET_LOCAL_CONTEXT",
                                        "order": 5,
                                        "context": {
                                            "key": "datosPagar",
                                            "type": "REMOTE_ACTION",
                                            "contexts": [
                                                {
                                                    "key": "clave",
                                                    "type": "VALUE",
                                                    "value": "MXM-DPJ-999"
                                                },
                                                {
