                },
                {
                    "order": 5,
                    "name": "files-uploader",
                    "componentType": "FIELD",
                    "component": {
                        "type": "FILE",
                        "save": true,
                        "restrictions": {
                            "maxNumberOfFiles": 3,
                            "minNumberOfFiles": 2,
                            "allowedFileTypes": [
                                ".pdf"
                            ]
                        }
                    },
                    "className": "col-span-2",
                    "events": {
                        "ON_CHANGE": [
                            {
                                "order": 5,
                                "type": "CONDITIONAL",
                                "conditions": {
                                    "equals": {
                                        "contextA": {
                                            "key": "fileUploaderProps",
                                            "type": "COMPONENT_VALUES",
                                            "componentName": "files-uploader",
                                            "expression": "canProceed"
                                        },
                                        "contextB": {
                                            "key": "true",
                                            "type": "VALUE",
                                            "value": true
                                        }
                                    }
                                },
                                "then": [
                                    {
                                        "order": 5,
                                        "type": "CONDITIONAL",
                                        "conditions": true,
                                        "actions": [
                                            {
                                                "type": "CONSOLE_LOG",
                                                "order": 5,
                                                "message": "Propiedades del uploader: {{fileUploaderProps | prettyjson}}",
                                                "contexts": [
                                                    {
                                                        "key": "fileUploaderProps",
                                                        "type": "COMPONENT_VALUES",
                                                        "componentName": "files-uploader",
                                                        "expression": "restrictions"
                                                    }
                                                ]
                                            }
                                        ]
                                    }
                                ],
                                "else": [
                                    {
                                        "order": 5,
                                        "type": "CONDITIONAL",
                                        "conditions": true,
                                        "actions": [
                                            {
                                                "type": "CONSOLE_LOG",
                                                "order": 5,
                                                "message": "Aguarda socio, total es {{totalFiles}}",
                                                "contexts": [
                                                    {
                                                        "key": "totalFiles",
                                                        "type": "COMPONENT_VALUES",
                                                        "componentName": "files-uploader",
                                                        "expression": "uploadStatus.total"
                                                    }
                                                ]
                                            }
                                        ]
                                    }
                                ]
                            },
                            {
                                "order": 10,
                                "type": "CONDITIONAL",
                                "conditions": true,
                                "actions": [
                                    {
                                        "type": "CLEAR_ERRORS",
                                        "order": 5,
                                        "fieldName": "files-uploader"
                                    }
                                ]
                            }
                        ],
                        "RESTRICTION_FAILED": [
                            {
                                "order": 5,
                                "type": "CONDITIONAL",
                                "conditions": {
                                    "lessThan": {
                                        "context": {
                                            "key": "totalUploaded",
                                            "type": "COMPONENT_VALUES",
                                            "componentName": "files-uploader",
                                            "expression": "uploadStatus.total"
                                        },
                                        "value": {
                                            "key": "minNumberFiles",
                                            "type": "COMPONENT_VALUES",
                                            "componentName": "files-uploader",
                                            "expression": "restrictions.minNumberOfFiles"
                                        }
                                    }
                                },
                                "then": [
                                    {
                                        "order": 5,
                                        "type": "CONDITIONAL",
                                        "conditions": true,
                                        "actions": [
                                            {
                                                "type": "ADD_ERROR",
                                                "order": 5,
                                                "fieldName": "files-uploader",
                                                "errorMessage": "Faltan subir archivos, por favor verifique que los archivos sean dos PDF"
                                            }
                                        ]
                                    }
                                ]
                            }
                        ]
                    }
                },
